package com.wjy.code;
/**
 * 判断magazine里的字符能否组成ransomNote
 * @author Jay Wang
 *
 */
public class CanConstruct {
	public static void main(String[] args) {
		System.out.println(CanConstruct.getCan("",
				""
						+""
						+""
				));
		}
	public static boolean getCan(String ransomNote,String magazine){
		 int [] vol = new int[26];
			for(int i=0;i<magazine.toCharArray().length;i++){
				vol[(magazine.charAt(i)-'a')]++;
			}
			for (int i = 0; i < ransomNote.toCharArray().length; i++) {
				if(--vol[ransomNote.charAt(i)-'a']<0)
					return false;
			}
			return true;
	}
}


